from customer_contract_settle_price
where enable = 1
AND history_flag = 0
AND customer_contract_id = #{customerContractId}
AND load_site_id = #{loadSiteId}
AND unload_site_id = #{unloadSiteId}
AND truck_type_id = #{truckTypeId}

SELECT

FROM customer_contract_settle_price
